<?php$a0 = 1;$a1 = 0;$a2 = 0;$b0 =0;$b1 =0;$b2 =0;function solve($a0, $a1, $a2, $b0, $b1, $b2){    if ($a0 > $b0) {        $a0 = 1;        return  $a0;    } elseif ($a0 < $b0) {        $b0 = 1;        return  $b0;    } elseif ($a0 == $b0) {        return  0;    }    if ($a1 > $b1) {        $a1 = 1;        return  $a1;    } elseif ($a1 < $b1) {        $b1 = 1;        return  $b1;    } elseif ($a1 == $b1) {        return  0;    }    if ($a2 > $b2) {        $a2 = 1;        return  $a2;    } elseif ($a2 < $b2) {        $b2 = 1;        return  $b2;    } elseif ($a2 == $b2) {        return  0;    }}$result = solve(1,2, 5, 3,3,6);echo $result;